INSERT INTO public.posts (id, user_id, title, slug, excerpt, body, original_link, image, created_at, updated_at, published_at, category_id, views, deleted_at, keywords, recommended, meta_id, "titlePreprocessed", "excerptPreprocessed", all_features_preprocessed, full_text, body_preprocessed, recommended_word2vec, recommended_word2vec_full_text, recommended_tfidf, recommended_tfidf_full_text, recommended_doc2vec, recommended_doc2vec_full_text, recommended_lda, recommended_lda_full_text, doc2vec_representation, trigrams_short_text, trigrams_full_text, recommended_word2vec_eval_1, recommended_word2vec_eval_2, recommended_word2vec_eval_3, recommended_word2vec_eval_4, recommended_word2vec_limited_fasttext, recommended_word2vec_limited_fasttest_full_text, recommended_word2vec_eval_cswiki_1, recommended_doc2vec_eval_cswiki_1, bert_vector_representation, recommended_test_prefilled_all) VALUES (1662, 22, 'Zemřel bez závěti a jeho dům zdědily dvě ženy, manželka i milenka', 'zemrel-bez-zaveti-a-jeho-dum-zdedily-dve-zeny-manzelka-i-milenka', 'Pokud neexistuje závěť, pozůstalost se rozdělí podle zákonných pravidel. Riziko tak hrozí zejména nesezdaným párům, partner totiž bude dědit pouze v případě, že zemřelý neměl děti a partneři žili ve společné domácnosti po dobu nejméně jednoho roku. Jenže jak ukazuje praxe, mohou nastat i kuriózní situace.', 'Pokud neexistuje závěť, pozůstalost se rozdělí podle zákonných pravidel. Riziko tak hrozí zejména nesezdaným párům, partner totiž bude dědit pouze v případě, že zemřelý neměl děti a partneři žili ve společné domácnosti po dobu nejméně jednoho roku. Jenže jak ukazuje praxe, mohou nastat i kuriózní situace.<p><a href=''https://www.idnes.cz/finance/financni-radce/nesezdani-partneri-dedictvi.A211019_125217_viteze_frp#utm_source=rss&utm_medium=feed&utm_campaign=fincentrum&utm_content=main''><b>Odkaz na původní článek</b></a></p>', 'https://www.idnes.cz/finance/financni-radce/nesezdani-partneri-dedictvi.A211019_125217_viteze_frp#utm_source=rss&utm_medium=feed&utm_campaign=fincentrum&utm_content=main', 'default.jpg', '2021-10-21 20:17:02.000000', '2021-10-21 20:17:02.000000', '2021-10-21 20:17:02.000000', 6, 0, null, 'zákonný pravidlo riziko, závěť, zemřelý nemít dítě, partner, neexistovat závěť pozůstalost, praxe nastat kuriózní, partner žít, společný domácnost', null, null, null, null, 'zemřelý nemít dítě nastat kuriózní neexistovat závěť závěť dům zdědit zákonný pravidlo partner totiž partner žít zemřít bez závěť a jeho dům zdědit dva žena manželka i milenka pokud neexistovat závěť pozůstalost se rozdělit podle zákonný pravidlo riziko tak hrozit zejména nesezdaný pár partner totiž být dědit pouze v případ že zemřelý nemít dítě a partner žít v společný domácnost po doba málo jeden rok jenže jak ukazovat praxe moct nastat i kuriózní situace', 'Pan Albert byl úspěšný podnikatel a šťastně ženatý muž. Ale ani štěstí, zejména to manželské, netrvá věčně. Pan Albert trávil spoustu času ve své firmě s asistentkou, která mu v pracovních věcech byla pravou rukou. Po čase mezi nimi přeskočila jiskra a tak pan Albert domů nespěchal, ani když pracovní věci byly dávno vyřešeny.
Jenže jeho manželka začala být podezřívavá, a když na něj udeřila, řekl jí pravdu. Má vztah se svou asistentkou. Manželka byla ale spořádaná žena a o rozvodu nechtěla ani slyšet. Domluvili se, že se rozvádět nebudou, ve společném domě zůstane bydlet pouze ona a nad to bude od manžela dostávat pravidelnou měsíční rentu.
Albert se s radostí přestěhoval do bytu své přítelkyně Emy, ta dokonce přestala chodit do práce, protože se to Albertovi už nadále nezdálo vhodné a také protože byl dost žárlivý. Celou domácnost tak živil pouze on. Jejich soužití trvalo přes dva roky. Jednou v noci se ale vracel ze služební cesty, auto dostalo nekontrolovatelný smyk, vyletělo ze silnice a narazilo do stromu. Albert nehodu nepřežil.
Bez závěti se dědí podle zákona
Po smrti pana Alberta se rozběhlo pozůstalostní řízení a vzhledem k tomu, že nebyla sepsána závěť ani uzavřena dědická smlouva, notář při rozdělování pozůstalosti postupoval podle zákona.
„Pokud není sepsaná závěť, nastupuje dědění podle zákona. Jako první na řadu v první třídě dědiců přicházejí děti a manžel/ka, kdy každý dědí stejným dílem,“ přibližuje advokát Pavel Strnad.
Pavel Strnad Absolvoval právnickou fakultu Masarykovy univerzity v Brně.
Studoval na Universitá La Sapienza v Římě a The John Marshall Law School v Chicagu.
Je vedoucím advokátem a zakladatelem AK Polverini Strnad.
Specializuje se na právo nemovitostí včetně bankovního financování a insolvenčního práva, zastřešuje službu Právní Ochrana Majetku (P.O.M.)
Protože pan Albert žádné děti neměl, notář postupoval dále a prověřoval druhou třídu dědiců. V té dědí manželka pozůstalého nejméně polovinu pozůstalosti a dále dědí zůstavitelovi rodiče a ti, kteří žili se zůstavitelem nejméně po dobu jednoho roku před jeho smrtí ve společné domácnosti a kteří z tohoto důvodu pečovali o společnou domácnost nebo byli odkázáni výživou na zůstavitele.
